SELECT A06_PRODUCT_KPI_COUPON.DATE_KEY,sum(A06_PRODUCT_KPI_COUPON.GOODS_COUPON_STOCK) as GOODS_COUPON_STOCK,sum(A06_PRODUCT_KPI_COUPON.GOODS_COUPON) as GOODS_COUPON,sum(A06_PRODUCT_KPI_COUPON.GOODS_USED_COUPON) as GOODS_USED_COUPON,sum(A06_PRODUCT_KPI_COUPON.MARKET_COUPON_STOCK) as MARKET_COUPON_STOCK,sum(A06_PRODUCT_KPI_COUPON.MARKET_COUPON) as MARKET_COUPON,sum(A06_PRODUCT_KPI_COUPON.MARKET_USED_COUPON) as MARKET_USED_COUPON FROM APP.A06_PRODUCT_KPI_COUPON as A06_PRODUCT_KPI_COUPON LEFT JOIN FFAN.DIM_DAY as DIM_DAY ON A06_PRODUCT_KPI_COUPON.DATE_KEY = DIM_DAY.DATEKEY Where DIM_DAY.DATEKEY >= '{{start}}' and DIM_DAY.DATEKEY <= '{{end}}' {{filters}} group by A06_PRODUCT_KPI_COUPON.DATE_KEY
order by A06_PRODUCT_KPI_COUPON.DATE_KEY desc