SELECT A06_PRODUCT_KPI_RED_LOTTERY.DATE_KEY,A06_PRODUCT_KPI_RED_LOTTERY.PRODUCT_NAME,sum(A06_PRODUCT_KPI_RED_LOTTERY.TOTAL_NUM) as TOTAL_NUM,sum(A06_PRODUCT_KPI_RED_LOTTERY.GET_NUM) as GET_NUM,sum(A06_PRODUCT_KPI_RED_LOTTERY.GET_USERS) as GET_USERS,sum(A06_PRODUCT_KPI_RED_LOTTERY.USED_NUM) as USED_NUM,sum(A06_PRODUCT_KPI_RED_LOTTERY.USED_USERS) as USED_USERS,sum(A06_PRODUCT_KPI_RED_LOTTERY.USED_MONEY) as USED_MONEY FROM APP.A06_PRODUCT_KPI_RED_LOTTERY as A06_PRODUCT_KPI_RED_LOTTERY  INNER JOIN FFAN.DIM_DAY as DIM_DAY ON A06_PRODUCT_KPI_RED_LOTTERY.DATE_KEY = DIM_DAY.DATEKEY where DIM_DAY.DATEKEY >='{{start}}' and DIM_DAY.DATEKEY <= '{{end}}' {{filters}} group by A06_PRODUCT_KPI_RED_LOTTERY.PRODUCT_NAME,A06_PRODUCT_KPI_RED_LOTTERY.DATE_KEY